async function migrate() {
  const client = new MongoClient(MONGO_URI);

  try {
    await client.connect();
    console.log('✅ Conectado a MongoDB');

    const db = client.db(DB_NAME);

    const collections = [
      { name: 'levels', data: levels },
      { name: 'competences', data: competences },
      { name: 'indicators', data: indicators },
      { name: 'tools', data: tools },
      { name: 'resources', data: resources },
      { name: 'referents', data: referents },
      { name: 'areas', data: areas },
      { name: 'resourceTypes', data: resourceTypes },
      // Relaciones
      { name: 'competenceIndicators', data: competenceIndicators },
      { name: 'competenceTools', data: competenceTools },
      { name: 'competenceResources', data: competenceResources },
      { name: 'competenceAreas', data: competenceAreas },
      { name: 'toolIndicators', data: toolIndicators },
      { name: 'toolResources', data: toolResources },
      { name: 'toolReferents', data: toolReferents },
      { name: 'resourceTypesRel', data: resourceTypesRel },
      { name: 'resourceReferents', data: resourceReferents },
    ];

    for (const col of collections) {
      const existing = await db.collection(col.name).countDocuments();
      if (existing > 0) {
        console.log(`⚠️  Colección '${col.name}' ya tiene ${existing} documentos. Saltando...`);
        continue;
      }
      await db.collection(col.name).insertMany(col.data);
      console.log(`📦 Colección '${col.name}' creada con ${col.data.length} documentos.`);
    }

    // Crear índices útiles
    await db.collection('competenceIndicators').createIndex({ id_competence: 1 });
    await db.collection('competenceIndicators').createIndex({ id_indicator: 1 });
    await db.collection('competenceTools').createIndex({ id_competence: 1 });
    await db.collection('competenceTools').createIndex({ id_tool: 1 });
    await db.collection('competenceAreas').createIndex({ id_competence: 1 });
    await db.collection('competenceAreas').createIndex({ id_area: 1 });
    await db.collection('toolIndicators').createIndex({ id_tool: 1 });
    await db.collection('toolReferents').createIndex({ id_tool: 1 });
    await db.collection('toolResources').createIndex({ id_tool: 1 });
    await db.collection('resourceReferents').createIndex({ id_resource: 1 });
    await db.collection('indicators').createIndex({ levelId: 1 });

    console.log('\n✅ Migración completada con éxito.');
  } catch (err) {
    console.error('❌ Error durante la migración:', err);
    process.exit(1);
  } finally {
    await client.close();
  }
}

migrate();